import { Component } from '@angular/core';
import { FormsModule } from '@angular/forms'; 
import { NgIf, NgFor } from '@angular/common'; 

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [FormsModule, NgIf, NgFor], 
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  nombre: string = '';  
  nombreIngresado: string = '';  
  mostrarBotonCanciones = false; 
  mostrarBotonCosasFavoritas = false;
  mostrarBotonCosasNoFavoritas = false;
  mostrarAdvertencia = false;
  mostrarCanciones = false;
  mostrarCosasFavoritas = false;
  mostrarCosasNoFavoritas = false;
  usuarios = [
    {
      nombre: 'Jesus',
      edad: 22,
      fechaNacimiento: '25-09-2002',
      universidad: 'Unipaz',
      carrera: 'ING Informatica',
      semestre: '5to',
      trabajo: 'Construccion',
      telefono: '3046206705',
      gustos: ['dormir', 'Viajar', 'lluvia'],
      cancionesFavoritas: [
        { titulo: 'Maleficio de sangre', url: 'https://youtu.be/tldw8gXCs58?list=RDtldw8gXCs58' },
        { titulo: 'Melodian', url: 'https://youtu.be/jghykH98Lj0?list=RDtldw8gXCs58' },
        { titulo: '7 years', url: 'https://youtu.be/kqFLQG484pg' },
        { titulo: 'Radioactive', url: 'https://youtu.be/w3viBe2Q0P8' },
        { titulo: 'Welcome to the circus', url: 'https://youtu.be/xD0E_Gj9xMk' },
        { titulo: 'We Are', url: 'https://youtu.be/GqMbGQx05jg' },
        { titulo: 'Engel', url: 'https://youtu.be/hNwMfgHcz5k' },
        { titulo: 'Siempre', url: 'https://youtu.be/g15jMZvvBIs' },
        { titulo: 'Naufrago', url: 'https://youtu.be/ZA-QzU7ETgg' },
        { titulo: 'Viva la vida', url: 'https://youtu.be/y4zdDXPYo0I' },
        { titulo: 'Canto de las sirenas', url: 'https://youtu.be/YeAdXdZd968' },
        { titulo: 'Pensando en ti', url: 'https://youtu.be/WnjfXA-PTH8' },
      ],
      cosasFavoritas: [
        { nombre: 'Pizza', imagen: 'https://www.shutterstock.com/image-vector/pizza-slice-doodle-icon-vector-260nw-2068741532.jpg' },
        { nombre: 'Picada', imagen: 'data:image/jpeg;base64,' },
        { nombre: 'Futbol', imagen: 'data:image/jpeg;base64,' },
        { nombre: 'Peliculas', imagen: 'data:image/jpeg;base64,' },
        { nombre: 'El campo', imagen: 'data:image/jpeg;base64,' },

      ],
      cosasNoFavoritas: [
        { nombre: 'Ensalada', imagen: 'data:image/jpeg;base64,' },
        { nombre: 'Cerveza', imagen: 'data:image/jpeg;base64,' },
        { nombre: 'Fumar', imagen: 'data:image/jpeg;base64,' },
        { nombre: 'Los insectos', imagen: 'data:image/jpeg;base64,' },
        { nombre: 'Lavar', imagen: 'data:image/jpeg;base64,' }
      ]
    }
  ];
  usuarioEncontrado: any = null;
  
  toggleCanciones() {
    this.mostrarCanciones = !this.mostrarCanciones;
  }

  toggleCosasFavoritas() {
    this.mostrarCosasFavoritas = !this.mostrarCosasFavoritas;
  }

  toggleCosasNoFavoritas() {
    this.mostrarCosasNoFavoritas = !this.mostrarCosasNoFavoritas;
  }

  enviarNombre() {
    if (this.nombre.trim() === '') {
      this.mostrarAdvertencia = true;
      this.mostrarBotonCanciones = false;
      this.mostrarBotonCosasFavoritas = false;
      this.mostrarBotonCosasNoFavoritas = false;
      this.usuarioEncontrado = null;
      this.nombreIngresado = '';
    } else {
      this.mostrarAdvertencia = false;
      this.nombreIngresado = this.nombre;
      this.usuarioEncontrado = this.usuarios.find(u => u.nombre.toLowerCase() === this.nombre.toLowerCase());
      
      if (this.usuarioEncontrado) {
        this.mostrarBotonCanciones = true;
        this.mostrarBotonCosasFavoritas = true;
        this.mostrarBotonCosasNoFavoritas = true;
      } else {
        this.usuarioEncontrado = null;
        this.mostrarBotonCanciones = false;
        this.mostrarBotonCosasFavoritas = false;
        this.mostrarBotonCosasNoFavoritas = false;
      }
    }
  }
}
